package cn.blmdz.third;

import com.alipay.api.AlipayClient;
import com.alipay.api.AlipayConstants;
import com.alipay.api.DefaultAlipayClient;

import cn.blmdz.web.third.AlipaySDK;

public class AliPayConfigTest {
	public static final String ALIPAY_APP_ID = "2016080700189958";
	private static final String GATEWAY_URL = "https://openapi.alipaydev.com/gateway.do";
	private static final String APP_PRIVATE_KEY = "";
	private static final String ALIPAY_PUBLIC_KEY = "";

	public static AlipaySDK getInstance() {
		AlipayClient alipayClient = new DefaultAlipayClient(
				AliPayConfigTest.GATEWAY_URL,
				AliPayConfigTest.ALIPAY_APP_ID,
				AliPayConfigTest.APP_PRIVATE_KEY,
				AlipayConstants.FORMAT_JSON,
				AlipayConstants.CHARSET_UTF8,
				AliPayConfigTest.ALIPAY_PUBLIC_KEY,
				AlipayConstants.SIGN_TYPE_RSA2);
		return new AlipaySDK(alipayClient);
	}
}
